    return new Promise(function(resolve, reject) {
        (function attempt(attemptsLeft) {
        	window.Citeck.Records.get(data).load('icase:caseStatusAssoc.cm:name', true).then(status => {
        		if (status !== null) {
        			return resolve(status);
        		}
        		if (attemptsLeft < 1) {
        			return resolve(null);
        		}
        		
        		setTimeout(attempt.bind(null, attemptsLeft - 1), period);
        	}).catch(reject);
        })(attemptsNumber);
    });
}

