association.invariant:
(function() {
    var parent = node.parent;
    while (parent && parent.hasClassName("activ:activity")) {
        parent = parent.parent;
    }
    var result = [];
    if (parent) {
        var levels = parent.assocs["req:completenessLevels"];
        for (var i in levels) result.push(levels[i]);
    }
    return result;
})()

