query getJournalRecords($journalId: String!, $query: String, $lang: String, $after: String, $first: Int) {
    journal(id: $journalId) {
        recordsConnection(after: $after, first: $first, q: $query, lang: $lang) {
            records {...recordsFields}
            totalCount
            pageInfo {
                endCursor
                hasNextPage
            }
        }
    }
}
