property.value:
query getJournalRecords($journalId: String!, $query: String, $lang: String, $pageInfo: InputJournalGqlPageInfoInput) {
    journal(id: $journalId) {
        recordsConnection(q: $query, lang: $lang, pageInfo: $pageInfo) {
            records {...recordsFields}
            totalCount
            pageInfo {
                hasNextPage
                skipCount
                maxItems
            }
        }
    }
}
